import React, { useState } from 'react';
import './TokenValidator.css';
import axios from 'axios';

function TokenValidator() {
  const [token, setToken] = useState('');
  const [validationResult, setValidationResult] = useState(null);
  const [formatError, setFormatError] = useState(null);

  const isTokenFormatValid = (token) => {
    // Token formatını kontrol eden regex
    const regex = /^\d{4}-\d{4}-\d{4}-\d{4}$/;
    return regex.test(token);
  };

  const handleSubmit = async () => {
    // Öncelikle format kontrolü
    if (!isTokenFormatValid(token)) {
      setFormatError('Token format is incorrect. It should be XXXX-XXXX-XXXX-XXXX.');
      setValidationResult(null); // Eğer format hatalıysa validationResult'ı temizleyin.
      return;
    }

    // Eğer format doğruysa, sunucuya gönder
    try {
      const response = await axios.post('http://localhost:8180/validateToken', { token });
      setValidationResult(response.data.isValid);
      setFormatError(null); // Format hatasını temizle
    } catch (error) {
      console.error('Error validating token:', error);
    }
  };

  return (
    <div>
      <h2>Token Validator</h2>
      <div>
        <label>
          Token:
          <input 
            type="text"
            value={token}
            onChange={e => setToken(e.target.value)}
          />
        </label>
      </div>
      <button onClick={handleSubmit}>Validate Token</button>
      {formatError && <p style={{color: 'red'}}>{formatError}</p>}
      {validationResult !== null && <p>{validationResult ? 'Valid Token!' : 'Invalid Token!'}</p>}
    </div>
  );
}

export default TokenValidator;
